import client from "./client";
import useLocation from "../hooks/useLocation";

const endpoint = "/get.php?action=get_lakes";

export const getLakes = (offset, limit, filter = "") => {
  //console.log("GetLakes Location = ", location);

  if (!limit) {
    limit = 10;
  }
  if (!offset) {
    offset = 0;
  }

  if (global.location) {
    var loc =
      "&lat=" + global.location.latitude + "&lng=" + global.location.longitude;
  }
  console.log("filter len", filter.length);
  if ((filter.length = 0)) {
    filter = "";
  } else {
    loc = "";
  }

  const endpoint_final =
    endpoint +
    "&offset=" +
    offset +
    "&limit=" +
    limit +
    "&filter=" +
    filter +
    loc;

  console.log("Endpoint=", endpoint_final);
  return client.get(endpoint_final);
};

export default {
  getLakes
};
